const raw = $json["output"] || $json["response"] || "";
const cleaned = raw
  .replace(/^```(json)?/i, "")
  .replace(/```$/i, "")
  .trim();

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  parsed = { error: "Invalid JSON", raw: cleaned };
}

// Normalize keys (lowercase)
const normalized = {};
for (const key in parsed) {
  normalized[key.toLowerCase()] = parsed[key];
}

return [normalized];

