// Get the Slack message text
const text = $input.first().json.text || '';

// Parse Summary
const summaryMatch = text.match(/Summary:\s*(.+?)(?=\n|$)/i);
const summary = summaryMatch ? summaryMatch[1].trim() : '';

// Parse Description (can be multiline, stops at next field)
const descMatch = text.match(/Description:\s*([\s\S]+?)(?=\nReporter:|$)/i);
const description = descMatch ? descMatch[1].trim() : '';

// Parse Reporter
const reporterMatch = text.match(/Reporter:\s*(.+?)(?=\n|$)/i);
const reporter = reporterMatch ? reporterMatch[1].trim() : '';

// Parse Priority
const priorityMatch = text.match(/Priority:\s*(.+?)(?=\n|$)/i);
const priority = priorityMatch ? priorityMatch[1].trim() : '';

// Parse Impact (optional)
const impactMatch = text.match(/Impact:\s*(.+?)(?=\n|$)/i);
const impact = impactMatch ? impactMatch[1].trim() : '';

// Parse Portal Tab (optional)
const portalTabMatch = text.match(/Portal Tab:\s*(.+?)(?=\n|$)/i);
const portalTab = portalTabMatch ? portalTabMatch[1].trim() : '';

// Return parsed data
return {
  json: {
    summary: summary,
    description: description,
    reporter: reporter,
    priority: priority,
    impact: impact,
    portalTab: portalTab,
    rawText: text // Keep original for debugging
  }
};

